        newrow.qty=data.message.items[i].qty;
        newrow.transfer_qty=data.message.items[i].qty;
        newrow.uom=data.message.items[i].uom;
        newrow.stock_uom=data.message.items[i].uom;
        newrow.conversion_factor=1;
